mapper.select:
	    SELECT      COUNT(*) 
        FROM 		course c
	    JOIN        professor p ON c.professor_id = p.professor_id
	    JOIN        dept d ON c.dept_id = d.dept_id
	    LEFT JOIN   registration r ON c.course_id = r.course_id AND r.student_id = #{studentId}
	    WHERE       c.course_status = 'OPEN'
	    AND   	    r.registration_id IS NULL

	        AND d.college = #{college}


	        AND c.dept_id = #{deptId}


	        AND c.course_id = #{courseId}


	        AND c.course_name LIKE CONCAT('%', #{courseName}, '%')

SELECT c.course_id, c.dept_id, c.professor_id, p.professor_name, c.course_name, c.credit_category, c.course_score, c.course_plan, c.course_current_enrollment, c.course_max_cnt, (SELECT CONCAT(ct.course_time_yoil, '/ ', ct.course_time_start, ' - ', ct.course_time_end) FROM course_time ct WHERE ct.course_id = c.course_id LIMIT 1) AS time_slot FROM course c JOIN professor p ON c.professor_id = p.professor_id JOIN dept d ON c.dept_id = d.dept_id LEFT JOIN registration r ON c.course_id = r.course_id AND r.student_id = #{searchDto.studentId} WHERE c.course_status = 'OPEN' AND r.registration_id IS NULL
AND d.college = #{searchDto.college}
AND c.dept_id = #{searchDto.deptId}
AND c.course_id = #{searchDto.courseId}
AND c.course_name LIKE CONCAT('%', #{searchDto.courseName}, '%')
LIMIT #{paginationDto.offset}, #{paginationDto.itemsPerPage}

mapper.update:
    	update  course
		set 	course_current_enrollment = #{enrollment}
		where 	course_id = #{courseId}

mapper.select:
SELECT course_current_enrollment, course_max_cnt
FROM course
where course_id = #{courseId}

mapper.delete:
DELETE FROM attendance
WHERE course_id = #{courseId}
and   student_id = #{studentId}

DELETE FROM score
WHERE student_id = #{studentId}
AND   course_id = #{courseId}
